loc = '/home/pi/photos/'  # Esta es la direccion donde se almacenan todas las imagenes. Esta variable es llamada en TakePicture.
code_min = 0              # Esta es la variable que limitara que el codigo QR no sea un numero mayor.   
code_max = 9999           # Esta es la variable que limitará que el codigo QR no sea un numero menor.
numSteps = 8             # Esta es la variable que va a definir cuantas fotos toma el escaner, y por consequencia, cuanto movera el motor cada vez y como nombrará las fotos.
